const api = axios.create({
    baseURL: 'http://localhost:8081', // URL-ul backend-ului tău Spring Boot
});


export const registerUser = (userData) => {
    return api.post('/users', userData);
};
export const loginUser = (userData) => {
    return api.post('/login', userData);
};

import axios from 'axios';
const API_URL = 'http://localhost:8081';


// Retrieve the token from localStorage
const token = localStorage.getItem('jwtToken');

console.log(token);

export const getPosts = (token) => {
    return axios.get(`${API_URL}/posts`, {
        headers: {
            Authorization: `Bearer ${token}`, // Correct header for authorization
        },
    });
};
const userId = localStorage.getItem('userId');
export const getPostsforUser = (token) => {
  return axios.get(`${API_URL}/user/${userId}`, {
      headers: {
          Authorization: `Bearer ${token}`, // Correct header for authorization
      },
  });
};

export const createPost = (postData, token) => {
    return axios.post(`${API_URL}/posts`, postData, {
        headers: {
            Authorization: `Bearer ${token}`, // Asigură-te că token-ul este trimis corect
        },
    });
};
export const updatePost = (postId, postData, token) => {
  return axios.put(`${API_URL}/posts/${postId}`, postData, {
    headers: {
      Authorization: `Bearer ${token}`, // Trimite token-ul
    },
  });
};

export const deletePost = (postId, token) => {
  return axios.delete(`${API_URL}/posts/${postId}`, {
    headers: {
      Authorization: `Bearer ${token}`, // Trimite token-ul
    },
  });
};
export const addComment = (postId, comment, token) => {
  const data = { content: comment };  // Define data with content
  return axios.post(`${API_URL}/posts/${postId}/comments`, data, {
    headers: {
      Authorization: `Bearer ${token}`, // Ensure token is sent correctly
    },
  })
  .then(response => {
    console.log('Comment added successfully!', response.data);
  })
  .catch(error => {
    console.error('Error adding comment:', error.response ? error.response.data : error);
  });
};
