import axios from 'axios';

const API_URL = 'http://localhost:8081'; // Backend-ul Spring Boot

const api = axios.create({
    baseURL: 'http://localhost:8081', // URL-ul backend-ului tău Spring Boot
});


export const getPosts = () => {
    return axios.get(`${API_URL}/posts`);
};

// export const createPost = (post) => {
//     return axios.post(`${API_URL}/posts`, post);
// };

export const getPostById = (id) => {
    return axios.get(`${API_URL}/posts/${id}`);
};

export const updatePost = (id, post) => {
    return axios.put(`${API_URL}/posts/${id}`, post);
};

export const deletePost = (id) => {
    return axios.delete(`${API_URL}/posts/${id}`);
};

// export const registerUser = (user) => {
//     return axios.post(`${API_URL}/users/register`, user);
// };

export const registerUser = (userData) => {
    return api.post('/users', userData);
};
export const loginUser = (userData) => {
    return api.post('/login', userData);
};
// export const loginUser = (user) => {
//     return axios.post(`${API_URL}/login`, user);
// };
export const getAllPublishedPostsForUser = (userId) => {
    return axios.get(`${API_URL}/posts${userId}`);
};
export const createPost = (post, token) => {
    return axios.post('/posts', post, {
      headers: {
        'Authorization': `Bearer ${token}`, // Adaugă token-ul în header
        'Content-Type': 'application/json'
      }
    });
  };
