import axios from 'axios';

const api = axios.create({
    baseURL: 'http://localhost:8080/api', // Backend
});

export default api;

export const getPosts = () => {
    return axios.get(`${API_URL}/posts`);
};

export const createPost = (post) => {
    return axios.post(`${API_URL}/posts`, post);
};

export const getPostById = (id) => {
    return axios.get(`${API_URL}/posts/${id}`);
};

export const updatePost = (id, post) => {
    return axios.put(`${API_URL}/posts/${id}`, post);
};

export const deletePost = (id) => {
    return axios.delete(`${API_URL}/posts/${id}`);
};

export const createUser = (user) => {
    return axios.post(`${API_URL}/users`, user);
};

export const getAllPublishedPostsForUser = (userId) => {
    return axios.get(`${API_URL}/posts/published/${userId}`);
};
